                BackgroundPane backgroundPane = new BackgroundPane(ImageIO.read(getClass().getResource(&quot;/images/Mando01-50.png&quot;)));
                backgroundPane.setBorder(new EmptyBorder(8, 8, 8, 8));
                backgroundPane.setLayout(new GridBagLayout());

                add(backgroundPane);

