public static class MySchoolCollector implements Collector&lt;Student, List&lt;Student&gt;, MySchool&gt; {

    private static final MySchoolCollector INSTANCE = new MySchoolCollector();

    @Override
    public Supplier&lt;List&lt;Student&gt;&gt; supplier() {
        return ArrayList::new;
    }

    @Override
    public BiConsumer&lt;List&lt;Student&gt;, Student&gt; accumulator() {
        return List::add;
    }

    @Override
    public BinaryOperator&lt;List&lt;Student&gt;&gt; combiner() {
        return (l1, l2) -&gt; {
            l1.addAll(l2);
            return l1;
        };
    }

    @Override
    public Function&lt;List&lt;Student&gt;, MySchool&gt; finisher() {
        return studentsList -&gt; {
            MySchool mySchool = new MySchool();
            studentsList.forEach(mySchool::addStudent);
            studentsList.forEach(student -&gt; mySchool.addStudentCount());
            return mySchool;
        };
    }

    @Override
    public Set&lt;Characteristics&gt; characteristics() {
        return Set.of(UNORDERED);
    }
}
