			String user = context.getInitParameter(&quot;dbUser&quot;);
			String password = context.getInitParameter(&quot;dbPassword&quot;);
			Class.forName(driver);
			connection = DriverManager.getConnection(url, user, password);

		} catch (ClassNotFoundException e) {
			e.printStackTrace();
			throw new UnavailableException(&quot;Can&#39;t load database driver&quot;);
		} catch (SQLException e) {
			e.printStackTrace();
			throw new UnavailableException(&quot;Couldn&#39;t get db connection&quot;);
		}

		ServletContext servletContext = getServletContext();
		ServletContextTemplateResolver templateResolver = new ServletContextTemplateResolver(servletContext);
		templateResolver.setTemplateMode(TemplateMode.HTML);
		this.templateEngine = new TemplateEngine();
		this.templateEngine.setTemplateResolver(templateResolver);
		templateResolver.setSuffix(&quot;.html&quot;);
	}
	/**
	 * @see HttpServlet#doGet(HttpServletRequest request, HttpServletResponse response)
	 */

	/**
	 * @see HttpServlet#doPost(HttpServletRequest request, HttpServletResponse response)
	 */
    @Override
	protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		String username = request.getParameter(&quot;username&quot;);
		String password = request.getParameter(&quot;password&quot;);
		String path=&quot;&quot;;
		boolean missing=false, invalid=false;
		HttpSession session=request.getSession();
		User user=null;
		UserDAO userDAO = new UserDAO(connection);
		ServletContext servletContext = getServletContext();
		final WebContext ctx = new WebContext(request, response, servletContext, request.getLocale());
		if(username == null || password == null) {
			missing=true;
			ctx.setVariable(&quot;missing&quot;, missing);
			session.invalidate();
			path=&quot;/WEB-INF/LoginPage.html&quot;;
			response.sendError(HttpServletResponse.SC_BAD_REQUEST, &quot;if user, pass null&quot;);
			templateEngine.process(path, ctx, response.getWriter());
		}
		else {
			try {
				user=userDAO.checkCredentials(username, password);
				if(user==null) {
					invalid=true;
					session.invalidate();
					ctx.setVariable(&quot;invalid&quot;, invalid);
					path=&quot;/WEB-INF/LoginPage.html&quot;;
					response.sendError(HttpServletResponse.SC_BAD_REQUEST, &quot;if no user&quot;);
					templateEngine.process(path, ctx, response.getWriter());

				}else {
					session.setAttribute(&quot;user&quot;, user);
					String ctxpath = getServletContext().getContextPath();
					path = ctxpath + &quot;/goToPlaylistPage&quot;;
					response.sendError(HttpServletResponse.SC_BAD_REQUEST, &quot;indirizzo verso la pagina GoToPlaylist&quot;);
					response.sendRedirect(path);
