public class PlaylistDAO {
	private Connection connection;
	
	public PlaylistDAO() {
		super();
	}
	
	public PlaylistDAO(Connection con) {
		this.connection = con;
	}
	
	public List&lt;Playlist&gt; getPlaylists(User user)throws SQLException{
		List&lt;Playlist&gt; p = new ArrayList&lt;Playlist&gt;();
		String query = &quot;SELECT * FROM playlist WHERE Creator = ?&quot;;
		ResultSet result = null;
		PreparedStatement pstatement = null;
		
		try {
			pstatement = connection.prepareStatement(query);
			pstatement.setString(1, user.getUsername());
			result=pstatement.executeQuery();
			while(result.next()) {
				Playlist pl = new Playlist();
				pl.setId(result.getInt(&quot;ID&quot;));
				pl.setOwner(result.getString(&quot;Creator&quot;));
				pl.setTitle(result.getString(&quot;Title&quot;));
				pl.setDate(result.getString(&quot;Date&quot;));
				p.add(pl);
			}
		}
		catch (SQLException e) {
		    e.printStackTrace();
			throw new SQLException(e);

		} finally {
			try {
				result.close();
			} catch (Exception e1) {
				throw new SQLException(e1);
			}
			try {
				pstatement.close();
			} catch (Exception e2) {
				throw new SQLException(e2);
			}
		}		
		return p;
	}
}
