    import java.util.Arrays;

    public static int farthestFromZero(int[] arr) {
        for (int i = 0; i &lt; arr.length; i++) {
            arr[i] = Math.abs(arr[i]);
        }
        Arrays.sort(arr);

        int max = 0;
        for (int i = 0; i &lt; arr.length-1; i++) {

