	    static int object_count = 0;
	
	    MyClass(){
		    object_count++;
	    }
	
	    MyClass(int i){
		
		    object_count++;
	    }
	
        void getCount() {
		
		    System.out.println(object_count);
	    }

	    public static void main(String... args) {
