```java
class ProductList extends ArrayList&lt;Product&gt; {
    @Override
    public boolean add(Product product) {
        return merge(product);
    }

    boolean merge(Product p) {
        for (Product v : this)
            if (p.getId() == v.getId()) {
                v.setPrice(p.getPrice() + v.getPrice());
                v.setQuantity(p.getQuantity() + v.getQuantity());
                return true;
            }
        return super.add(p);
    }
}
