    fun addImageToGallery(b: Bitmap): Uri {
        val resolver = context.applicationContext.contentResolver

        val pictureCollection = MediaStore.Images.Media
            .getContentUri(MediaStore.VOLUME_EXTERNAL_PRIMARY)

