```
@Component
public class SolrClientFactoryImpl{

	private final HttpClient httpClient;

	@Autowired
	public SolrClientFactoryImpl(final HttpClient httpClient) {
		this.httpClient = httpClient;
	}

	@Override
	public SolrClientWrapperImpl getSolrClient(final ZookeeperConnectionSpecifier zk) {
		logger.debug(&quot;Creating CloudSolrClient for cluster {}&quot;, zk.getZkConnectionString());
		return new SolrClientWrapperImpl(
