                if (ch == &#39;-&#39; &amp;&amp; (i == 0 || tokens.isEmpty() || tokens.get(tokens.size() - 1).equals(&quot;(&quot;))) {
                    tokens.add(Character.toString(ch));
                } else if (ch != &#39; &#39;) {
                    tokens.add(Character.toString(ch));
                }

            }
        }

        if (sb.length() &gt; 0) {
            tokens.add(sb.toString());
        }
        return tokens;
