                int o0 = (b0 &lt;&lt; 2) | (b1 &gt;&gt;&gt; 4);
                int o1 = ((b1 &amp; 0xf) &lt;&lt; 4) | (b2 &gt;&gt;&gt; 2);
                int o2 = ((b2 &amp; 3) &lt;&lt; 6) | b3;
                out[op++] = (byte) o0;
                if (op &lt; oLen)
                    out[op++] = (byte) o1;
                if (op &lt; oLen)
                    out[op++] = (byte) o2;
