```java
import org.eclipse.swt.SWT;
import org.eclipse.swt.graphics.GC;
import org.eclipse.swt.graphics.Image;
import org.eclipse.swt.graphics.ImageData;
import org.eclipse.swt.graphics.ImageLoader;
import org.eclipse.swt.graphics.Point;
import org.eclipse.swt.widgets.Composite;

import java.nio.file.Path;

final class GIFWidget extends Composite {
    GIFWidget(Composite parent, Path gifFilePath) {
        super(parent, SWT.NONE);
        this.gifFilePath = gifFilePath.toAbsolutePath();
    }

    void show() {
        if (!this.isAnimating) {
            this.isAnimating = true;
            this.startAnimationThread();
        }
    }

    void hide() {
        this.isAnimating = false;
    }

    private void startAnimationThread() {
        GC gc = new GC(this);
        Thread animationThread = new Thread(() -&gt; {
            while (this.isAnimating &amp;&amp; !this.isDisposed()) {
                try {
                    this.getDisplay().asyncExec(() -&gt;
                        this.drawNextImage(gc)
                    );
                    int delayTime = this.getCurrentDelayTime();
                    Thread.sleep(delayTime * DELAY_TIME_MULTIPLIER);
                } catch (InterruptedException e) {
                    break;
                }
            }
            if (!this.isDisposed()) {
                this.getDisplay().asyncExec(() -&gt; {
                    this.clearDrawing(gc);
                    gc.dispose();
                });
            }
        });
        animationThread.setDaemon(true);
        animationThread.start();
    }

    private void drawNextImage(GC gc) {
        ImageLoader imageLoader = this.getImageLoader();
        if (imageLoader.data != null) {
            ImageData imageData = imageLoader.data[this.imageIndex];
            this.drawImage(gc, imageData);
            this.imageIndex = (this.imageIndex + 1) % imageLoader.data.length;
        }
    }

