    public class P45 {
    
    	public static void main(String[] args) {
    		long H = 0;
    		long i = 144;
    		while(true) {
    			H = i*((i&lt;&lt;1)-1);
    			if ( isPentagonal(H) &amp;&amp; isTriangle(H) ) {
    				break;
    			}
