    	private static boolean isPentagonal(long x) {
    		double n = (1 + Math.sqrt(24*x+1)) / 6;
    		return n == (long)n;
    	}
    	
    	private static boolean isTriangle(long x) {
    		double n = (-1 + Math.sqrt((x&lt;&lt;3)+1)) / 2;
    		return n == (long)n;
    	}
    	
