        iss.start();
    }

    static class InputStreams{
        BufferedReader p;
        BufferedReader p2;

        public InputStreams(BufferedReader p, BufferedReader p2) {
            this.p = p;
            this.p2 = p2;
        }

        List&lt;Consumer&lt;String&gt;&gt; listeners = new ArrayList&lt;&gt;();

        void start(){
            start(p);
            start(p2);
        }

        void start(BufferedReader br){
            new Thread(() -&gt; {
                while(true){
                    try {
                        if (!br.ready()) break;
                        notifyNextString(br.readLine());
                    } catch (IOException e) {
                        throw new RuntimeException(e);
                    }
                }
            }).start();
        }

        void addNextStringListener(Consumer&lt;String&gt; listener){
            listeners.add(listener);
        }

        void notifyNextString(String nextString){
            listeners.forEach(l -&gt; l.accept(nextString));
        }
    }
