Class MediaControl (**MediaControl.java**)

    import javafx.application.Platform;
	import javafx.beans.InvalidationListener;
	import javafx.beans.Observable;
	import javafx.event.ActionEvent;
	import javafx.event.EventHandler;
	import javafx.scene.layout.Priority;
	import javafx.scene.layout.Region;
	import javafx.scene.layout.StackPane;
	import javafx.geometry.Insets;
	import javafx.geometry.Pos;
	import javafx.scene.control.Button;
	import javafx.scene.control.Label;
	import javafx.scene.control.Slider;
	import javafx.scene.layout.BorderPane;
	import javafx.scene.layout.HBox;
	import javafx.scene.media.MediaPlayer;
	import javafx.scene.media.MediaPlayer.Status;
	import javafx.scene.media.MediaView;
	import javafx.util.Duration;
	
	public class MediaControl extends BorderPane {

    private MediaPlayer mp;
    private MediaView mediaView;
    private final boolean repeat = false;
    private boolean stopRequested = false;
    private boolean atEndOfMedia = false;
    private Duration duration;
    private Slider timeSlider;
    private Label playTime;
    private Slider volumeSlider;
    private HBox mediaBar;

    public MediaControl(final MediaPlayer mp) {
		this.mp = mp;
	    setStyle(&quot;-fx-background-color: #bfc2c7;&quot;);
	    MediaView mediaView = new MediaView(mp);
	    StackPane mvPane = new StackPane() {
	    };
	    
	    mvPane.getChildren().add(mediaView);
	    mvPane.setStyle(&quot;-fx-background-color: black;&quot;);
	    mediaView.fitWidthProperty().bind(mvPane.widthProperty());
	    mediaView.fitHeightProperty().bind(mvPane.heightProperty());
	    mediaView.setPreserveRatio(true);
	    setCenter(mvPane);
	    
	    mediaBar = new HBox();
	    mediaBar.setAlignment(Pos.CENTER);
	    mediaBar.setPadding(new Insets(5, 10, 5, 10));
	    BorderPane.setAlignment(mediaBar, Pos.CENTER);

	    final Button playButton = new Button(&quot;&gt;&quot;);
	    

	    playButton.setOnAction(new EventHandler&lt;ActionEvent&gt;() {
	        public void handle(ActionEvent e) {
	            Status status = mp.getStatus();

	            if (status == Status.UNKNOWN || status == Status.HALTED) {
	                // don&#39;t do anything in these states
	                return;
	            }

	            if (status == Status.PAUSED
	                    || status == Status.READY
	                    || status == Status.STOPPED) {
	                // rewind the movie if we&#39;re sitting at the end
	                if (atEndOfMedia) {
	                    mp.seek(mp.getStartTime());
	                    atEndOfMedia = false;
	                }
	                mp.play();
	            } else {
	                mp.pause();
	            }
	        }
	    });

	    mp.currentTimeProperty().addListener(new InvalidationListener() {
	        public void invalidated(Observable ov) {
	            updateValues();
	        }
	    });

	    mp.setOnPlaying(new Runnable() {
	        public void run() {
	            if (stopRequested) {
	                mp.pause();
	                stopRequested = false;
	            } else {
	                playButton.setText(&quot;||&quot;);
	            }
	        }
	    });

	    mp.setOnPaused(new Runnable() {
	        public void run() {
	            System.out.println(&quot;onPaused&quot;);
	            playButton.setText(&quot;&gt;&quot;);
	        }
	    });

	    mp.setOnReady(new Runnable() {
	        public void run() {
	            duration = mp.getMedia().getDuration();
	            System.out.println(&quot;Duration: &quot;+duration);
	            updateValues();
	        }
	    });

	    mp.setCycleCount(repeat ? MediaPlayer.INDEFINITE : 1);
	    mp.setOnEndOfMedia(new Runnable() {
	        public void run() {
	            System.out.println(&quot;Entre&quot;);
	            if (!repeat) {
	                playButton.setText(&quot;&gt;&quot;);
	                stopRequested = true;
	                atEndOfMedia = true;
	            }
	        }
	    });

	    mediaBar.getChildren().add(playButton);
	    // Add spacer
	    Label spacer = new Label(&quot;   &quot;);
	    mediaBar.getChildren().add(spacer);

	    // Add Time label
	    Label timeLabel = new Label(&quot;Tiempo: &quot;);
	    mediaBar.getChildren().add(timeLabel);

	    // Add time slider
	    timeSlider = new Slider();
	    HBox.setHgrow(timeSlider, Priority.ALWAYS);
	    timeSlider.setMinWidth(50);
	    timeSlider.setMaxWidth(Double.MAX_VALUE);
	    timeSlider.valueProperty().addListener(new InvalidationListener() {
	        public void invalidated(Observable ov) {
	            if (timeSlider.isValueChanging()) {
	                // multiply duration by percentage calculated by slider position
	                mp.seek(duration.multiply(timeSlider.getValue() / 100.0));
	            }
	        }
	    });
	    mediaBar.getChildren().add(timeSlider);
	    
	    Label spacer1 = new Label(&quot;   &quot;);
	    mediaBar.getChildren().add(spacer1);
	    
	    // Add Play label
	    playTime = new Label();
	    playTime.setPrefWidth(130);
	    playTime.setMinWidth(50);
	    mediaBar.getChildren().add(playTime);

	    // Add the volume label
	    Label volumeLabel = new Label(&quot;Vol: &quot;);
	    mediaBar.getChildren().add(volumeLabel);

	    // Add Volume slider
	    volumeSlider = new Slider(0,100,100);
	    volumeSlider.setPrefWidth(70);
	    volumeSlider.setMaxWidth(Region.USE_PREF_SIZE);
	    volumeSlider.setMinWidth(30);
	    volumeSlider.valueProperty().addListener(new InvalidationListener() {
	        public void invalidated(Observable ov) {
	            if (volumeSlider.isValueChanging()) {
	                mp.setVolume(volumeSlider.getValue() / 100.0);
	            }
	        }
	    });
	    mediaBar.getChildren().add(volumeSlider);

	    setBottom(mediaBar);
	}

	protected void updateValues() {
	    if (playTime != null &amp;&amp; timeSlider != null &amp;&amp; volumeSlider != null) {
	        Platform.runLater(new Runnable() {
	            public void run() {
	                duration = mp.getMedia().getDuration();
	                Duration currentTime = mp.getCurrentTime();
	                playTime.setText(formatTime(currentTime, duration));
	                timeSlider.setDisable(duration.isUnknown());
	                if (!timeSlider.isDisabled()
	                        &amp;&amp; duration.greaterThan(Duration.ZERO)
	                        &amp;&amp; !timeSlider.isValueChanging()) {
	                    timeSlider.setValue(currentTime.divide(duration).toMillis()
	                            * 100.0);
	                }
	                if (!volumeSlider.isValueChanging()) {
	                    volumeSlider.setValue((int) Math.round(mp.getVolume()
	                            * 100));
	                }
	            }
	        });
	    }
	}

	private static String formatTime(Duration elapsed, Duration duration) {
	    int intElapsed = (int) Math.floor(elapsed.toSeconds());
	    int elapsedHours = intElapsed / (60 * 60);
	    if (elapsedHours &gt; 0) {
	        intElapsed -= elapsedHours * 60 * 60;
	    }
	    int elapsedMinutes = intElapsed / 60;
	    int elapsedSeconds = intElapsed - elapsedHours * 60 * 60
	            - elapsedMinutes * 60;

	    if (duration.greaterThan(Duration.ZERO)) {
	        int intDuration = (int) Math.floor(duration.toSeconds());
	        int durationHours = intDuration / (60 * 60);
	        if (durationHours &gt; 0) {
	            intDuration -= durationHours * 60 * 60;
	        }
	        int durationMinutes = intDuration / 60;
	        int durationSeconds = intDuration - durationHours * 60 * 60
	                - durationMinutes * 60;
	        if (durationHours &gt; 0) {
	            return String.format(&quot;%d:%02d:%02d/%d:%02d:%02d&quot;,
	                    elapsedHours, elapsedMinutes, elapsedSeconds,
	                    durationHours, durationMinutes, durationSeconds);
	        } else {
	            return String.format(&quot;%02d:%02d/%02d:%02d&quot;,
	                    elapsedMinutes, elapsedSeconds, durationMinutes,
	                    durationSeconds);
	        }
	    } else {
	        if (elapsedHours &gt; 0) {
	            return String.format(&quot;%d:%02d:%02d&quot;, elapsedHours,
	                    elapsedMinutes, elapsedSeconds);
	        } else {
	            return String.format(&quot;%02d:%02d&quot;, elapsedMinutes,
	                    elapsedSeconds);
	        }
	    }
	}
    }


