    	public void add(Integer item) {
    		if (getLastIndex() == arr.length) {
    			generateBiggerArray();
    			return;
    		}
    		for (int i = 0; i &lt; arr.length; i++) {
    			if (arr[i] == null) {
    				arr[i] = item;
    				break;
    			}
    		}
