                                    // System.err.println(&quot;Ignoring: &quot; + protocol
                                    // + &quot; protocol in &quot; + urlLink);
                                    continue;
                            }
                            addNewDescription(urlLink.toString());
                    }
            }
    
            private void extractResults(String text) {
                    Pattern p = Pattern
                                    .compile(&quot;([\\w\\-]([\\.\\w])+[\\w]+@([\\w\\-]+\\.)+[A-Za-z]{2,4})&quot;);
