        int width = getWidth() - 1;
        int height = getHeight() - 1;

        int oldX = 0;
        int oldY = height / 2;
        int frame = 0;

        // min, max is the min/max range of the samples, ie the highest and lowest samples
        int range = max + (min * -2);
        float scale = (float) height / (float) range;

        int minY = Math.round(((height / 2) + (min * scale)));
