        final String turnId = context.getBroadcastState(BROADCAST_DESCRIPTOR).get(event.getTurnId());
        final long expirationTimestamp = nextTimeoutWindow(context.timestamp(), Time.minutes(1));

        if (turnId == null) {
            // Maybe the event is safe to collect, or the turnId has not been broadcast yet. Save it for later.
            this.eventsToReprocessState.add(event);

            context.timerService().registerEventTimeTimer(expirationTimestamp);
        }

    }

    @Override
    public void processBroadcastElement(String turnId, Context context, Collector&lt;OutputEvent&gt; out) throws Exception {
        context.getBroadcastState(BROADCAST_DESCRIPTOR).put(turnId, turnId);
    }

    @Override
    public void onTimer(long timestamp, OnTimerContext ctx, Collector&lt;OutputEvent&gt; out) throws Exception {
        super.onTimer(timestamp, ctx, out);
        int count = 0;
        int size = 0;
        final Iterable&lt;EventC&gt; eventsToReprocess = this.eventsToReprocessState.get();
        if (eventsToReprocess != null) {
            for (EventC event: eventsToReprocess) {
                size++;
                if (!ctx.getBroadcastState(BROADCAST_DESCRIPTOR).contains(event.getTurnId())) {
                    final OutputEvent OutputEvent = generateOutputEvent(event);
                    out.collect(OutputEvent);
                    count++;
                }
            }
            eventsToReprocessState.clear();
        }

    }

    private OutputEvent generateOutputEvent(final EventC event) {
        return OutputEvent.builder()
                ...
                .build();
    }

    public static long nextTimeoutWindow(final long timestamp, final Time interval) {
        return endOfWindow(timestamp, interval) + interval.toMilliseconds();
    }

    public static long beginningOfWindow(final long timestamp, final Time interval) {
        return timestamp - (timestamp % interval.toMilliseconds());
    }

    public static long endOfWindow(final long timestamp, final Time interval) {
        return beginningOfWindow(timestamp, interval) + interval.toMilliseconds();
    }
```

