    }

    public void update(){

        if(this.isRunning == true){

            if(this.isContinuous == true){

                if(frameTimer == 14){
                    if(currentFrame &lt; frames.frameCount){
                        currentFrame++;
                        frameTimer -= 14;
                    }else if(currentFrame == frames.frameCount){
                        currentFrame = 1;
                        frameTimer -= 14;
                    }

                }else{
                    frameTimer++;
                }

            }else if(this.isContinuous == false){

                if(frameTimer == 14){
                    if(currentFrame &lt; frames.frameCount){
                        currentFrame++;
                        frameTimer -= 14;
                    }else if(currentFrame == frames.frameCount){
                        currentFrame = 1;
                        frameTimer -= 14;
                        this.stop();
                    }

                }else{
                    frameTimer++;
                }

            }

        }

    }

    public void start(){

        frameTimer = 0;
        isRunning = true;

    }

    public void stop(){

        frameTimer = 0;
        currentFrame = 1;
