		for(i=0;i&lt;n;i++){
			num[i]=sc.nextInt();
		}
		
		for(i=1;i&lt;n;i++){
			tmp=num[i];
			for(j=0;j&lt;i;j++){
				if(num[i-j-1]&gt;tmp){
					tmp2=num[i-j-1];
					num[i-j-1]=tmp;
					num[i-j]=tmp2;
				}
