int m, mn, n;
String[] B = new String[n = A.length];
boolean f;
for (m = 0; m &lt; n; m++)
    for (String a : A[m]) {
        f = false;
        for (mn = m + 1; mn &lt; n; mn++) {
            for (String b : A[mn])
                if (a.equals(b)) {
                    f = true;
                    break;
                }
            if (f) break;
        }
        if (!f) B[m] = a;
    }
