				.collect(Collectors.groupingBy(EventlogRow::getCaseId));

		Map&lt;String, Integer&gt; commaSeparatedEventsNameToCaseIdsCountMap = getCommaSeparatedEventNamesWithCaseIdCount(caseIdsEventLogRowsMap);

		Map&lt;String, Integer&gt; topEventVariants = getTopEventVariants(limit, commaSeparatedEventsNameToCaseIdsCountMap);

		EventVariantResponse eventVariantResponse = new EventVariantResponse();

		topEventVariants.forEach((eventNames, caseCount) -&gt; {
					EventVariant eventVariant = new EventVariant(eventNames, caseCount);
					eventVariantResponse.getVariants().add(eventVariant);
				}
		);

		return objectMapper.writeValueAsString(eventVariantResponse);

	}

	private void validateInput(EventlogRow eventlogRow) {
		if (Objects.isNull(eventlogRow.getCaseId()) || Objects.isNull(eventlogRow.getEventName()) || Objects.isNull(eventlogRow.getTimestamp())) {
			throw new RequiredFieldValuesMissingException();
		}
	}

	private Map&lt;String, Integer&gt; getCommaSeparatedEventNamesWithCaseIdCount(Map&lt;String, List&lt;EventlogRow&gt;&gt; caseIdsEventLogRowsMap) {
		Map&lt;String, Integer&gt; commaSeparatedEventsNameToCaseIdsCountMap = new HashMap&lt;&gt;();

		caseIdsEventLogRowsMap.forEach((CaseId, caseIdEventLogs) -&gt; {
			caseIdEventLogs.sort(Comparator.comparing(EventlogRow::getTimestamp));
			String commaSeparatedEventNames = caseIdEventLogs.stream().distinct().map(EventlogRow::getEventName).collect(Collectors.joining(&quot;,&quot;));
			int count = commaSeparatedEventsNameToCaseIdsCountMap.getOrDefault(commaSeparatedEventNames, 0);

			commaSeparatedEventsNameToCaseIdsCountMap.put(commaSeparatedEventNames, ++count);
		});

		return commaSeparatedEventsNameToCaseIdsCountMap;
	}

	private Map&lt;String, Integer&gt; getTopEventVariants(int limit, Map&lt;String, Integer&gt; commaSeparatedEventsNameToCaseIdsCountMap) {
		return commaSeparatedEventsNameToCaseIdsCountMap.entrySet().stream()
				.sorted(Map.Entry.comparingByValue(Comparator.reverseOrder()))
				.limit(limit).collect(Collectors.toMap(Map.Entry::getKey, Map.Entry::getValue, (e1, e2) -&gt; e1, LinkedHashMap::new));
	}
