    function slideshow() {
        // reset slide index counter if it exceeds the maximum limit
        if (index &gt;= slides.length) index = 0;
        
        // show the current slide
        if (!slides[index].classList.contains(&#39;show&#39;))
            slides[index].classList.add(&#39;show&#39;);

