    public static void serializeDirectory(Directory directory, OutputStream outputStream, int bufferSize) throws IOException {
        try (var os = new ZipOutputStream(outputStream)){
            var buffer = new byte[bufferSize];
            for (var file : directory.listAll()) {

                var entry = new ZipEntry(file);
                try (var input = directory.openInput(file, IOContext.READONCE)) {
                    os.putNextEntry(entry);

                    for (var position = 0; position &lt; input.length(); position += buffer.length) {
                        var len = (int) Math.min(input.length() - position, buffer.length);

                        input.readBytes(buffer, 0, len);
                        os.write(buffer, 0, len);
                    }

                }
                os.closeEntry();

            }
        }
    }

    public static &lt;T extends Directory&gt; T deserializeDirectory(Supplier&lt;T&gt; directoryFactory, InputStream inputStream, int bufferSize) throws IOException {
        var directory = directoryFactory.get();
