        if(Mark&gt;=0 &amp;&amp; Mark&lt;=MaxMark) return Mark;

        _error =&quot;Line (&quot;+ErrorlineNumber+&quot;): &quot;+MarkName +&quot; must be a value between 0 and &quot;+ MaxMark +&quot;!&quot;;
