            return id;
        }
    
        public void setId(int id) {
            this.id = id;
        }
    
        @NotNull
        public Integer getCatagory_Id() {
            return catagory_Id;
        }
    
        public void setCatagory_Id(@NotNull Integer catagory_Id) {
            this.catagory_Id = catagory_Id;
        }
    
        @NotNull
