		if (n &gt; 1) {
			Matrix I = new Matrix(m - 1, n - 1);

			for (int i = 1; i &lt; m; i++)
				for (int j = 1; j &lt; n; j++)
					I.setValue(i - 1, j - 1, M.getValue(i, j));

			D = M.getValue(0, 0) * getMatrixDet(I);
