		EmbeddedKey key = new EmbeddedKey();
		key.setGroupNo(1);

		Book book = new Book();
		book.setBookName( &quot;High-Performance Java Persistence&quot;);

		book.setKey(key);

		entityManager.persist(book);
	});

	doInJPA(entityManager -&gt; {
		EmbeddedKey key = new EmbeddedKey();

		key.setGroupNo(1);
		key.setRowId(1L);

