        borderStylingItems.add(_BoldFont.BASE_LINE.code); break;
      case _Border.THIN:
        borderStylingItems.add(_ThinFont.BASE_LINE.code); break;
      case _Border.DOUBLE:
        borderStylingItems.add(_DoubleFont.BASE_LINE.code); break;
