    LOWER_LEFT_CORNER(&#39;\u2517&#39;),
    LOWER_RIGHT_CORNER(&#39;\u251B&#39;);

    private final char code;

    BoldFont(char code) {

      this.code = code;
    }
  }

  private enum ThinFont {

    FACE_LINE(&#39;\u2502&#39;),
    BASE_LINE(&#39;\u2500&#39;),
    UPPER_LEFT_CORNER(&#39;\u250C&#39;),
    UPPER_RIGHT_CORNER(&#39;\u2510&#39;),
    MIDDLE_LEFT(&#39;\u252C&#39;),
    MIDDLE_RIGHT(&#39;\u2524&#39;),
    LOWER_LEFT_CORNER(&#39;\u2514&#39;),
    LOWER_RIGHT_CORNER(&#39;\u2518&#39;);

    private final char code;

    ThinFont(char code) {

      this.code = code;
    }
  }

  private enum DoubleFont {

    FACE_LINE(&#39;\u2551&#39;),
