    final String apiKeysPath = &quot;/properties/api_keys.properties&quot;;
    final InputStream res = MyClass.class.getResourceAsStream(apiKeysPath);

    final Properties properties = new Properties();
    try {
        properties.load(res);
    } catch (IOException e) {
