```java
private final Map&lt;Long, Long&gt; timestamps = new HashMap&lt;&gt;();

@Override
public void onMessageReceived(MessageReceivedEvent event) {
    if (!event.getContentRaw().equals(&quot;&gt;deadchat&quot;)) return;
    long channelId = event.getChannel().getIdLong();
    long now = System.currentTimeMillis();
    Long lastUsed = timestamps.get(channelId);
    // Check how long ago the timestamp was
    if (lastUsed != null &amp;&amp; now - lastUsed &lt; TimeUnit.HOURS.toMillis(1)) {
        return; // cooldown of 1 hour
    }
    // Update timestamp to now, next use only permitted in an hour
    timestamps.put(channelId, now);
    
