       testLibs = &quot;${rootProject.projectDir}/test.libs&quot;
    }
    dependencies {
       compile files(&quot;${libs}/activation.jar&quot;, &quot;${libs}/imap.jar&quot;)
       compile files(&quot;${testLibs}/gson-2.2.4.jar&quot;)
       ...
    }


