                (bytes[offset + Integer.BYTES - 2] &amp; 0xFF) &lt;&lt; Byte.SIZE |
                (bytes[offset + Integer.BYTES - 3] &amp; 0xFF) &lt;&lt; Byte.SIZE * 2 |
