    private &lt;T&gt; List&lt;List&lt;T&gt;&gt; nPartition(List&lt;T&gt; objs, final int N) {
		return new ArrayList&lt;&gt;(IntStream.range(0, objs.size()).boxed().collect(
				Collectors.groupingBy(e-&gt;e%N,Collectors.mapping(e-&gt;objs.get(e), Collectors.toList())
