                InputStreamReader isr = new InputStreamReader(is);
    
                int data = isr.read();
                while (data != -1) {
                    current += (char) data;
                    data = isr.read();
                }
            } catch (MalformedURLException e) {
                e.printStackTrace();
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                if(urlConnection != null) {
                    urlConnection.disconnect();
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        return current;
