			String commaSeparatedEventNames = caseIdEventLogs.stream().distinct().map(EventlogRow::getEventName).collect(Collectors.joining(&quot;,&quot;));
			int count = commaSeparatedEventsNameToCaseIdsCountMap.getOrDefault(commaSeparatedEventNames, 0);

			commaSeparatedEventsNameToCaseIdsCountMap.put(commaSeparatedEventNames, ++count);
