            SimpleDateFormat sdf = new SimpleDateFormat(&quot;EEE MMM dd yyyy HH:mm:ss &#39;GMT&#39;Z&quot;, Locale.ENGLISH);
            String created = &quot;Thu Feb 04 2016 09:32:14 GMT+0100&quot;;
            Date date = sdf.parse(created);
