    org.joda.time.DateTime parseDate(String maybeDate, String format) {
      org.joda.time.DateTime date = null;
      try {
        DateTimeFormatter fmt = DateTimeFormat.forPattern(format);
        date =  fmt.parseDateTime(maybeDate);
      } catch (Exception e) { }
      return date;
    }


