    public final class ObjectUtils {

        private static final Gson GSON = new GsonBuilder().setPrettyPrinting().create();

        private ObjectUtils() {
             throw new UnsupportedOperationException(&quot;Instantiation of this class is not permitted in case you are using reflection.&quot;);
