            secondGluePanel.add(Box.createHorizontalGlue(), BorderLayout.CENTER);
            secondGluePanel.add(Box.createVerticalGlue(), BorderLayout.SOUTH);
            
            mainPanel.add(firstGluePanel);
            mainPanel.add(secondGluePanel);
