				rbs = r1 - abs(i);

				rsum += r[yi] * rbs;
				gsum += g[yi] * rbs;
				bsum += b[yi] * rbs;

				if (i &gt; 0) {
					rinsum += stack[ir][0];
					ginsum += stack[ir][1];
					binsum += stack[ir][2];
				} else {
					routsum += stack[ir][0];
					goutsum += stack[ir][1];
					boutsum += stack[ir][2];
				}

				if (i &lt; hm) {
					yp += w;
				}
			}
			yi = x;
			stackpointer = radius;
			for (y = 0; y &lt; h; y++) {
				output[yi].red = dv[rsum];
				output[yi].green = dv[gsum];
				output[yi].blue = dv[bsum];

				rsum -= routsum;
				gsum -= goutsum;
				bsum -= boutsum;

				stackstart = stackpointer - radius + div;
				ir = stackstart % div; // same as sir

				routsum -= stack[ir][0];
				goutsum -= stack[ir][1];
				boutsum -= stack[ir][2];

				if (x == 0) vmin[y] = min(y + r1, hm) * w;
				ip = x + vmin[y];

				stack[ir][0] = r[ip];
				stack[ir][1] = g[ip];
				stack[ir][2] = b[ip];

				rinsum += stack[ir][0];
				ginsum += stack[ir][1];
				binsum += stack[ir][2];

				rsum += rinsum;
				gsum += ginsum;
				bsum += binsum;

				stackpointer = (stackpointer + 1) % div;
				ir = stackpointer; // same as sir

				routsum += stack[ir][0];
				goutsum += stack[ir][1];
				boutsum += stack[ir][2];

				rinsum -= stack[ir][0];
				ginsum -= stack[ir][1];
				binsum -= stack[ir][2];

				yi += w;
			}
		}
		
		// Unlocks everything
		AndroidBitmap_unlockPixels(env, bitmapIn);
		AndroidBitmap_unlockPixels(env, bitmapOut);

		LOGI (&quot;Bitmap blurred.&quot;);
	}

	int min(int a, int b) {
		return a &gt; b ? b : a;
	}

	int max(int a, int b) {
		return a &gt; b ? a : b;
	}

