    	/**
    	 * Color names.
    	 */
    	public static final String [] humanColors = {
    			&quot;unrecognized&quot;, 
    			&quot;black&quot;, //black colors[1]
    			&quot;grey&quot;,
    			&quot;light_grey&quot;,
    			&quot;red&quot;,
    			&quot;orange&quot;,
    			&quot;yellow&quot;,
    			&quot;jasny_zielony&quot;,
    			&quot;zielony&quot;,
    			&quot;zielony&quot;,
    			&quot;turkusowy&quot;,
    			&quot;jasny_niebieski&quot;,
    			&quot;niebieski&quot;,
    			&quot;fioletowy&quot;,
    			&quot;fioletowy&quot;,
    			&quot;rozowy&quot;,
    			&quot;czerwony&quot;,
    			&quot;bialy&quot;,
    		};
    	
    	/**
    	 * Convert from  RGB do HSV
    	 * 
    	 * @param r
    	 * @param g
    	 * @param b
    	 * @param hsv
    	 */
    	public static void rgb2hsv(int r, int g, int b, int hsv[]) {
    
    		int min; // Min. value of RGB
    		int max; // Max. value of RGB
    		int delMax; // Delta RGB value
    
    		if (r &gt; g) {
    			min = g;
    			max = r;
    		} else {
    			min = r;
    			max = g;
    		}
    		if (b &gt; max)
    			max = b;
    		if (b &lt; min)
    			min = b;
    
    		delMax = max - min;
    
    		float H = 0, S;
    		float V = max;
    
    		if (delMax == 0) {
    			H = 0;
    			S = 0;
    
    		} else {
    			S = delMax / (float) max;
    			if (r == max)
    				H = ((g - b) / (float) delMax) * 60;
    			else if (g == max)
    				H = (2 + (b - r) / (float) delMax) * 60;
    			else if (b == max)
    				H = (4 + (r - g) / (float) delMax) * 60;
    
    		}
    
    		hsv[0] = (int) (H);
    		hsv[1] = (int) (S * 100);
    		hsv[2] = (int) V;
    	}
    
    	/**
    	 * Return human color index {@link Color#colors}
    	 * 
    	 * @param r
    	 * @param g
    	 * @param b
    	 * @return
    	 */
    	public static int [] getHumanColor(int r, int g, int b) {
    		int [] res = {0, 0, 0, 0};
    		int[] hsv = new int[3];
    		rgb2hsv(r, g, b, hsv);
    		for (int i = 1; i &lt; colors.length; i++) {
    			if (hsv[0] &lt;= colors[i][2] &amp;&amp; hsv[0] &gt;= colors[i][1]
    					&amp;&amp; hsv[1] &lt;= colors[i][4] &amp;&amp; hsv[1] &gt;= colors[i][3]
    					&amp;&amp; hsv[2] &lt;= colors[i][6] &amp;&amp; hsv[2] &gt;= colors[i][5]) {
    				res[3] = i;
    				res[0] = hsv[0];
    				res[1] = hsv[1];
    				res[2] = hsv[2];
    				return res;
    			}
    
    		}
    		return res;
    	}
    
    }

