package it.mirkoscotti.log4j.poi;

import org.apache.poi.xssf.usermodel.XSSFWorkbook;

public class Log4jPoi
{

	public Log4jPoi(String path)
	{
		try (var input = ClassLoader.getSystemResourceAsStream(path))
		{
			new XSSFWorkbook(input);
		}
		catch (Exception x)
		{
			throw new IllegalArgumentException(x);
		}
	}

	public static void main(String[] args)
	{
		new Log4jPoi(&quot;it/mirkoscotti/log4j/poi/excel.xlsx&quot;);
	}
}
