    public void evaluatePermissions(Object resource, String permission) {
        Authentication authentication = SecurityContextHolder.getContext().getAuthentication();

        if (evaluatePagePermissions(authentication, resource, permission)) {
            return;
        }

        resource = convertToEntity(resource);

        if (!permissionEvaluator.hasPermission(authentication, resource, permission)) {
            throw accessDeniedException;
        }
