     */
    protected Object convertOptionalToEntity(Object resource) {
        if (resource instanceof Optional) {
            Optional&lt;?&gt; optional = (Optional&lt;?&gt;) resource;
            if (optional.isPresent()) {
                return optional.get();
            }
        }
        return resource;
    }
