    private int partitioning(int [] array,  int first, int last){
        int randPivot = rand.nextInt(last - first) + first;
        int pivot = array[randPivot];
        int i = first - 1;
        swap(array, randPivot, last);
    
        for (int j = first; j &lt;= last - 1; j++){
            if (array[j]&lt;= pivot){
                i++;
                swap(array, i, j);
            }
        }
        swap(array, i+1, last);
        return i+1;
    }

    private void quickSorting(int [] array, int first, int last){
