    private static void bubbleSort(String[] arr, int n) {

        for (int i = 0; i &lt; n - 1; ++i)
        {
            for (int j = 0; j &lt; n - i - 1; ++j)
            {
                if (arr[j + 1].compareTo(arr[j]) &lt; 0)
                {
                    String temp = arr[j + 1];
                    arr[j + 1] = arr[j];
                    arr[j] = temp;
                }
            }
        }
    }
}
